mapper.select:
select A.* from ( select rownum as rnum, B.* from ( select * from boardtab
order by num desc) B
where rownum <= #{end}) A where A.rnum >= #{start}

mapper.update:
update boardtab set subject = #{subject}, mail = #{mail}, 
			content = #{content}, wip = #{wip}, wdate = sysdate
	where num = #{num}
